import React, { useState } from 'react';
import {
  Box,
  Container,
  Typography,
  TextField,
  Button,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
  Grid
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Signup = () => {
  const [nom, setNom] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [age, setAge] = useState('');
  const [genre, setGenre] = useState('homme');
  const [type, setType] = useState('parent');
  const [photo, setPhoto] = useState(null);

  const navigate = useNavigate();

  const handleFileChange = (e) => {
    setPhoto(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('nom', nom);
    formData.append('email', email);
    formData.append('password', password);
    formData.append('age', age);
    formData.append('genre', genre);
    formData.append('type', type);
    if (photo) {
      formData.append('photo', photo);
    }

    try {
      const response = await fetch('http://localhost:8000/Signup.php', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        alert(data.message);
        navigate('/');
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error('Erreur rÃ©seau :', error);
      alert("Une erreur s'est produite lors de l'inscription.");
    }
  };

  return (
    <Box sx={{ minHeight: '100vh', py: 8, background: 'linear-gradient(to right, #4b0082, #1e90ff)', color: 'white' }}>
      <Container maxWidth="sm">
        <Box
          component="form"
          onSubmit={handleSubmit}
          sx={{
            background: 'rgba(255,255,255,0.1)',
            p: 4,
            borderRadius: 4,
            backdropFilter: 'blur(10px)',
            boxShadow: 3,
          }}
        >
          <Typography variant="h4" gutterBottom align="center">
            Inscription Ã  la villa connectÃ©e
          </Typography>

          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                label="Nom"
                value={nom}
                onChange={(e) => setNom(e.target.value)}
                fullWidth
                required
                variant="filled"
                InputProps={{ style: { backgroundColor: 'white' } }}
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                label="Email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                fullWidth
                required
                variant="filled"
                InputProps={{ style: { backgroundColor: 'white' } }}
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                label="Mot de passe"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                fullWidth
                required
                variant="filled"
                InputProps={{ style: { backgroundColor: 'white' } }}
              />
            </Grid>

            <Grid item xs={6}>
              <TextField
                label="Ãge"
                type="number"
                value={age}
                onChange={(e) => setAge(e.target.value)}
                fullWidth
                required
                variant="filled"
                InputProps={{ style: { backgroundColor: 'white' } }}
              />
            </Grid>

            <Grid item xs={6}>
              <FormControl fullWidth variant="filled">
                <InputLabel>Genre</InputLabel>
                <Select
                  value={genre}
                  onChange={(e) => setGenre(e.target.value)}
                  required
                  style={{ backgroundColor: 'white' }}
                >
                  <MenuItem value="homme">Homme</MenuItem>
                  <MenuItem value="femme">Femme</MenuItem>
                  <MenuItem value="autre">Autre</MenuItem>
                </Select>
              </FormControl>
            </Grid>

            <Grid item xs={12}>
              <FormControl fullWidth variant="filled">
                <InputLabel>RÃ´le dans la maison</InputLabel>
                <Select
                  value={type}
                  onChange={(e) => setType(e.target.value)}
                  required
                  style={{ backgroundColor: 'white' }}
                >
                  <MenuItem value="parent">Parent (propriÃ©taire)</MenuItem>
                  <MenuItem value="enfant">Enfant</MenuItem>
                  <MenuItem value="invitÃ©">InvitÃ©(e)</MenuItem>
                  <MenuItem value="intendant">Majordome / Intendant</MenuItem>
                  <MenuItem value="jardinier">Jardinier / JardiniÃ¨re</MenuItem>
                  <MenuItem value="mÃ©nage">EmployÃ©(e) de maison</MenuItem>
                  <MenuItem value="sÃ©curitÃ©">Agent de sÃ©curitÃ©</MenuItem>
                  <MenuItem value="autre">Autre</MenuItem>
                </Select>
              </FormControl>
            </Grid>

            <Grid item xs={12}>
              <label style={{ fontWeight: 'bold', display: 'block', marginBottom: 6 }}>
                Photo de profil :
              </label>
              <label
                htmlFor="upload-photo"
                style={{
                  display: 'inline-block',
                  padding: '10px 20px',
                  backgroundColor: 'white',
                  color: '#4b0082',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontWeight: 'bold'
                }}
              >
                Choisir une image
              </label>
              <input
                id="upload-photo"
                type="file"
                name="photo"
                accept="image/*"
                onChange={handleFileChange}
                style={{ display: 'none' }}
              />
            </Grid>

            <Grid item xs={6}>
              <Button type="submit" fullWidth variant="contained" color="primary">
                Envoyer
              </Button>
            </Grid>

            <Grid item xs={6}>
              <Button
                fullWidth
                variant="outlined"
                color="inherit"
                onClick={() => navigate('/')}
              >
                Retour Ã  lâaccueil
              </Button>
            </Grid>
          </Grid>
        </Box>
      </Container>
    </Box>
  );
};

export default Signup;
