import React, { useState, useEffect } from 'react';

function ThermostatControls({ device }) {
  const [currentTemp, setCurrentTemp] = useState(device?.currentTemp ?? 20);
  // const [battery, setBattery] = useState(device?.battery ?? 100); // Commenté car plus utilisé
  // const [mode, setMode] = useState(device?.mode ?? 'batterie'); // Commenté car plus utilisé
  const [temperatureStarted, setTemperatureStarted] = useState(false);
  const [heatingOn, setHeatingOn] = useState(false); // État pour activer/désactiver le chauffage



  useEffect(() => {
    const timeout = setTimeout(() => {
      setTemperatureStarted(true);
    }, 5000);

    const interval = setInterval(() => {
      if (temperatureStarted && typeof device.targetTemp === 'number') {
        setCurrentTemp(prev => {
          const diff = device.targetTemp - prev;
          const step = diff > 0 ? 0.2 : (diff < 0 ? -0.1 : 0);
          return parseFloat((prev + step).toFixed(1));
        });
      }


    }, 1000);

    return () => {
      clearInterval(interval);
      clearTimeout(timeout);
    };
  }, [device?.targetTemp, temperatureStarted, device?.name]); 

  // Fonction pour ajuster la température
  const handleTempChange = (e) => {
    device.targetTemp = parseFloat(e.target.value);
  };

  // Fonction pour allumer/éteindre le chauffage
  const toggleHeating = () => {
    setHeatingOn(prevState => !prevState);
  };

  return (
    <div className="thermostat-controls">

      {/* Affichage de la molette de réglage de température seulement si le chauffage est allumé */}
      {heatingOn && (
        <div className="temperature-control">
          <label htmlFor="tempRange">Réglage de la température</label>
          <input
            id="tempRange"
            type="range"
            min="10"
            max="30"
            step="0.5"
            value={device.targetTemp}
            onChange={handleTempChange}
          />
          <span>{device.targetTemp}°C</span>
        </div>
      )}


      <button 
        className="switch"
        onClick={toggleHeating}
        style={{ marginBottom: '10px' }}
      >
        {heatingOn ? 'Éteindre chauffage' : 'Allumer chauffage'}
      </button>
    </div>
  );
}

export default ThermostatControls;
