import React, { useState, useEffect } from 'react';
import '../styles/SmartDeviceCard.css';
import ThermostatControls from './ThermostatControls';
import LampControls from './LampControls';

function SmartDeviceCard({ device }) {
  const [currentTemp, setCurrentTemp] = useState(device?.currentTemp ?? 20);
  const [battery, setBattery] = useState(device?.battery ?? 100);
  const [mode, setMode] = useState(device?.mode ?? 'batterie');
  const [temperatureStarted, setTemperatureStarted] = useState(false);

  const updateBattery = (mode, battery) => {
    if (mode === 'batterie') {
      return battery > 0 ? battery - 0.1 : 0;
    } else if (mode === 'branché') {
      return battery < 100 ? battery + 0.1 : 100;
    }
    return battery;
  };

  useEffect(() => {
    if (!device) return;

    const timeout = setTimeout(() => {
      setTemperatureStarted(true);
    }, 5000);

    const interval = setInterval(() => {
      if (temperatureStarted && typeof device.targetTemp === 'number') {
        setCurrentTemp(prev => {
          const diff = device.targetTemp - prev;
          const step = diff > 0 ? 0.2 : (diff < 0 ? -0.1 : 0);
          return parseFloat((prev + step).toFixed(1));
        });
      }

      setBattery(prev => {
        const newBattery = updateBattery(mode, prev);
        localStorage.setItem(`battery-${device.name}`, newBattery.toFixed(1));
        return parseFloat(newBattery.toFixed(1));
      });
    }, 1000);

    return () => {
      clearInterval(interval);
      clearTimeout(timeout);
    };
  }, [mode, device?.targetTemp, temperatureStarted, device?.name]);

  // Retour JSX — fallback intégré ici
  if (!device) {
    return <div className="smart-device-card">Aucun appareil sélectionné</div>;
  }

  return (
    <div className="smart-device-card">
      <h3>{device.name || 'Nom inconnu'}</h3>
      <p><strong>Temp. actuelle :</strong> {currentTemp.toFixed(1)}°C</p>
      <p><strong>Temp. cible :</strong> {typeof device.targetTemp === 'number' ? `${device.targetTemp.toFixed(1)}°C` : 'N/A'}</p>
      <p><strong>Mode :</strong> {mode}</p>
      <p><strong>Connectivité :</strong> {device.connectivity || 'Non spécifiée'}</p>
      <p><strong>Batterie :</strong> {battery.toFixed(1)}%</p>
      <p><strong>Dernière interaction :</strong> {device.lastSeen || 'Inconnue'}</p>
      <p className={`status ${device.status ? device.status.toLowerCase() : ''}`}>
        <strong>État :</strong> {device.status || 'Inconnu'}
      </p>

      <br />
      <hr />

      <div className="temperature-control">
        <label htmlFor="tempRange"><strong>Réglage de la température</strong></label>
        <input
          id="tempRange"
          type="range"
          min="10"
          max="30"
          step="0.5"
          value={typeof device.targetTemp === 'number' ? device.targetTemp : 20}
          onChange={(e) => device.targetTemp = parseFloat(e.target.value)}
        />
        <span>{typeof device.targetTemp === 'number' ? `${device.targetTemp.toFixed(1)}°C` : 'N/A'}</span>
      </div>

      <br />
      <div className="power-mode">
        <label htmlFor="powerMode">Mode d'alimentation</label>
        <select
          id="powerMode"
          value={mode}
          onChange={(e) => setMode(e.target.value)}
        >
          <option value="batterie">Batterie</option>
          <option value="branché">Branché</option>
        </select>
      </div>
    </div>
  );
}

export default SmartDeviceCard;
