import React, { useState, useEffect } from 'react';
import '../styles/Auth.css';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [role, setRole] = useState('');
    const [users, setUsers] = useState([]);
    const [attente, setAttente] = useState([]);

    // Nouveaux états pour afficher l'email après connexion
    const [isLoggedIn, setIsLoggedIn] = useState(false); // Vérifie si l'utilisateur est connecté
    const [userEmail, setUserEmail] = useState('');
    const [loginError, setLoginError] = useState(''); 

    useEffect(() => {
      if (role === 'admin') {
        //fetchUsersAndAttente();
        fetchUsers();
      }
    }, [role]);
    const fetchUsers = () => {
      const adminEmail = 'admin@example.com';  // L'email de l'admin que vous souhaitez envoyer

      console.log("📤 Envoi de l'email admin :", adminEmail);  // Affiche l'email que vous envoyez

      // Envoi de la requête avec l'email de l'admin
      fetch("http://localhost:8000/GetUsers.php", {
        method: "POST",  // Méthode POST pour envoyer des données
        headers: {
          "Content-Type": "application/x-www-form-urlencoded"  // Indique que les données sont envoyées sous forme de formulaire
        },
        body: new URLSearchParams({
          'admin_email': adminEmail  // Assurez-vous d'envoyer l'email ici
        })
      })
      .then(response => response.text())  // Traite la réponse du serveur comme du texte brut
      .then(data => {
        console.log("📥 Réponse brute du serveur:", data);  // Affiche la réponse brute du serveur

        // Si la réponse contient des données, on les met à jour
        if (data) {
          setUsers([data]);  // Mets à jour les utilisateurs avec la réponse brute
        } else {
          console.error("❌ Réponse vide ou invalide");
          setUsers([]);  // Si la réponse est vide, on vide la liste des utilisateurs
        }
      })
      .catch(error => {
        console.error("❌ Erreur récupération utilisateurs:", error);  // Affiche l'erreur s'il y en a une
        setUsers([]);  // En cas d'erreur, on vide la liste des utilisateurs
      });
    };

   /*
