import React, { useState } from 'react';

function SearchBar({ onSearch }) {
  const [searchTerm, setSearchTerm] = useState('');
  const [status, setSearchStatus] = useState(''); // '', 'places', 'events' ou 'information'
  const [filter, setFilter] = useState('');

  const handleSearch = (e) => {
    e.preventDefault();
    onSearch({
      term: searchTerm,
      type: status,
      filter: filter
    });
  };

  return (
    <div className="search-container">
      <form onSubmit={handleSearch} className="search-form">
        <div className="search-inputs">
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Rechercher..."
            className="search-input"
          />

          <select
            value={status}
            onChange={(e) => setSearchStatus(e.target.value)}
            className="search-select"
          >
            <option value="">Catégorie</option>
            <option value="on">Appareils actifs</option>
            <option value="off">Appareils éteints</option>
          </select>

          <select
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            className="search-select"
          >
            {status=== 'places' ? (
              <>
                <option value="all">Toutes les catégories</option>
                <option value="park">Parcs</option>
                <option value="museum">Musées</option>
                <option value="monument">Monuments</option>
              </>
            ) : status === 'events' ? (
              <>
                <option value="all">Toutes les périodes</option>
                <option value="today">Aujourd'hui</option>
                <option value="week">Cette semaine</option>
                <option value="month">Ce mois</option>
              </>
            ) : (
              <>
                <option value="all">Toutes les catégories</option>
                <option value="news">Actualités</option>
                <option value="services">Services municipaux</option>
                <option value="procedures">Démarches administratives</option>
              </>
            )}
          </select>

          <button type="submit" className="search-button">
            Rechercher
          </button>
        </div>
      </form>
    </div>
  );
}

export default SearchBar;
