import React, { useState } from 'react';
import '../styles/Auth.css';
import AdminDashboard from '/home/cytech/web/projetx/src/components/AdminDashboard.jsx';



function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userEmail, setUserEmail] = useState('');
  const [role, setRole] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Crée les données à envoyer
    const data = new URLSearchParams();
    data.append('email', email);
    data.append('password', password);

    fetch('http://localhost:8000/Login.php', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: data,
    })
      .then((response) => response.text()) // Récupérer la réponse en texte brut
      .then((data) => {
        if (data.startsWith('success')) {
          // Si la connexion est réussie
          const [, userEmail, userRole] = data.split('|');
          setIsLoggedIn(true);
          setUserEmail(userEmail);
          setRole(userRole);
        } else {
          alert(data); // Affiche l'erreur
        }
      })
      .catch((error) => {
        console.error('Erreur:', error);
      });
  };

  return (
    <div>
      {!isLoggedIn ? (
        <form onSubmit={handleSubmit}>
          <label>
            Email:
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </label>
          <br />
          <label>
            Mot de passe:
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </label>
          <br />
          <br />
          <button type="submit">LogIn</button>
        </form>
      ) : (
        <div>
          <h2>Connexion réussie !</h2>
          <p>Email : {userEmail}</p>
          <p>Role : {role}</p>
        </div>
      )}
    </div>
  );
}

export default Login;
