import React, { useState } from 'react';

function CameraControls() {
  // État pour gérer l'affichage de l'image de la caméra
  const [isCameraActive, setIsCameraActive] = useState(false);

  // Fonction pour activer la caméra (afficher l'image)
  const handleActivateCamera = () => {
    setIsCameraActive(true);
  };

  // Fonction pour désactiver la caméra (cacher l'image)
  const handleDeactivateCamera = () => {
    setIsCameraActive(false);
  };

  return (
    <div className="camera-controls">
      <h3>Contrôles de la Caméra</h3>

      {/* Affichage conditionnel de l'image de la caméra */}
      {isCameraActive && (
        <div className="camera-image">
          <img 
            src="https://th.bing.com/th/id/R.affa1ced418e8234d38d4df6888dddad?rik=dX3cEKCRkPB6mQ&riu=http%3a%2f%2flecomptoirdeviolette.fr%2fwp-content%2fuploads%2f2017%2f07%2fD%c3%a9coration-en-bois-maison.jpg&ehk=z0JinVv6aQ5NorRwOaSL879E2CP1Wz7ygy3%2fLeBPGCs%3d&risl=&pid=ImgRaw&r=0"
            alt="Caméra"
            style={{ width: '100%', height: 'auto' }}
          />
        </div>
      )}

      {/* Boutons pour activer et désactiver la caméra */}
      <div className="camera-controls-buttons">
        <button onClick={handleActivateCamera}>Activer la caméra</button>
        <button onClick={handleDeactivateCamera}>Désactiver la caméra</button>
      </div>
    </div>
  );
}

export default CameraControls;
