import React, { useState, useEffect } from "react";

const Profile = ({ userEmail }) => {
  const [nom, setNom] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
    fetch("http://localhost:8000/Profile.php", {
      method: "POST",
      headers: { "Content-Type": "application/x-www-form-urlencoded" },
      body: new URLSearchParams({ email: userEmail }), // Envoie l'email de l'utilisateur
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.nom) {
          setNom(data.nom);
        }
      })
      .catch((err) => console.error("Erreur:", err));
  }, [userEmail]);

  const handleUpdate = (e) => {
    e.preventDefault();

    fetch("http://localhost:8000/UpdateProfile.php", {
      method: "POST",
      headers: { "Content-Type": "application/x-www-form-urlencoded" },
      body: new URLSearchParams({
        email: userEmail,
        nom: nom,
        password: password, // L'utilisateur peut laisser vide s'il ne veut pas changer
      }),
    })
      .then((res) => res.text())
      .then((data) => alert(data))
      .catch((err) => console.error("Erreur:", err));
  };

  return (
    <div>
      <h2>Profil</h2>
      <form onSubmit={handleUpdate}>
        <label>
          Nom :
          <input type="text" value={nom} onChange={(e) => setNom(e.target.value)} />
        </label>
        <br />
        <label>
          Nouveau mot de passe (optionnel) :
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </label>
        <br />
        <button type="submit">Mettre Ã  jour</button>
      </form>
    </div>
  );
};

export default Profile;
