import React, { useState } from 'react';
import '../styles/Auth.css';

function Login() {
  const [nom, setNom] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [age, setAge] = useState('');
    const [genre, setGenre] = useState('');
    const [message, setMessage] = useState('');
    // Nouveaux états pour afficher l'email après connexion
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [userEmail, setUserEmail] = useState('');

    const handleSubmit = (e) => {
      e.preventDefault();
    // Affichage des valeurs avant l'envoi
    console.log("Données envoyées : ", { nom, email, password, age, genre, message });

      // Crée les données à envoyer
      const data = new URLSearchParams();
      data.append('nom', nom);
      data.append('email', email);
      data.append('password', password);
      data.append('age', age);
      data.append('genre', genre);

      // recupere des données au serveur PHP
      fetch("http://localhost:8000/LogIn.php", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: data,
      })
      .then((response) => response.text() )// Lire la réponse sous forme de texte
      .then((data) => {
        console.log('Réponse du serveur:', data);
        if (data.status === 'success') {
          setIsLoggedIn(true);
          setUserEmail(data.email); // Mettre l'email de l'utilisateur dans l'état
        } else {
          alert(data.message);  // Affiche le message d'erreur
        }
        /*
        if (data.status === "error") {
          alert(data.message);  // Affiche le message d'erreur
        } else {
          alert("Inscription réussie !");
        }
        */
      })
      .catch((error) => console.error("Erreur:", error));
    };
    return (
      <form onSubmit={handleSubmit}>
        <label>
          Email :
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </label>
        <br />
        <label>
          Mot de passe :
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </label>
        <br />
        <br />
        <button type="logIn">LogIn</button>
      </form>
    );
}

export default Login;
