import React, { useState } from 'react';

function Form() {
  const [nom, setNom] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [age, setAge] = useState('');
  const [genre, setGenre] = useState('');
  const [message, setMessage] = useState('');
  const [emailError, setEmailError] = useState(""); // État pour l'erreur email

  const handleSubmit = (e) => {
    e.preventDefault();
  // Affichage des valeurs avant l'envoi
  console.log("Données envoyées : ", { nom, email, password, age, genre, message });

    // Crée les données à envoyer
    const data = new URLSearchParams();
    data.append('nom', nom);
    data.append('email', email);
    data.append('password', password);
    data.append('age', age);
    data.append('genre', genre);

    // recupere des données au serveur PHP
    fetch("http://localhost:8000/submit.php", {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: data,
    })
    .then((response) => {
      console.log('Response:', response); // Log la réponse brute
      return response.text(); // Lire la réponse sous forme de texte
    })
    .then((data) => {
      console.log(data);
      if (data.status === "error") {
        alert(data.message);  // Affiche le message d'erreur
      } else {
        alert("Inscription réussie !");
      }
    })
    .catch((error) => console.error("Erreur:", error));
  };
  return (
    <form onSubmit={handleSubmit}>
      <label>
        Nom :
        <input type="text" value={nom} onChange={(e) => setNom(e.target.value)} />
      </label>
      <br />
      <label>
        Email :
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      </label>
      <br />
      <label>
        Mot de passe :
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </label>
      <br />
      <label>
        Âge :
        <input type="number" value={age} onChange={(e) => setAge(e.target.value)} />
      </label>
      <br />
      <label>
        Genre :
        <select value={genre} onChange={(e) => setGenre(e.target.value)}>
          <option value="homme">Homme</option>
          <option value="femme">Femme</option>
          <option value="autre">Autre</option>
        </select>
      </label>
      <br />
      <label>
        Message :
        <textarea value={message} onChange={(e) => setMessage(e.target.value)} />
      </label>
      <br />
      <button type="submit">Envoyer</button>
    </form>
  );
}

export default Form;
