import React, { useState, useEffect } from 'react';

function ThermostatControls({ device }) {
  const [currentTemp, setCurrentTemp] = useState(device?.currentTemp ?? 20);
  const [battery, setBattery] = useState(device?.battery ?? 100);
  const [mode, setMode] = useState(device?.mode ?? 'batterie');
  const [temperatureStarted, setTemperatureStarted] = useState(false);
  const [heatingOn, setHeatingOn] = useState(false); // État pour activer/désactiver le chauffage

  // Mise à jour de la batterie selon le mode
  const updateBattery = (mode, battery) => {
    if (mode === 'batterie') {
      return battery > 0 ? battery - 0.1 : 0;
    } else if (mode === 'branché') {
      return battery < 100 ? battery + 0.1 : 100;
    }
    return battery;
  };

  useEffect(() => {
    const timeout = setTimeout(() => {
      setTemperatureStarted(true);
    }, 5000);

    const interval = setInterval(() => {
      if (temperatureStarted && typeof device.targetTemp === 'number') {
        setCurrentTemp(prev => {
          const diff = device.targetTemp - prev;
          const step = diff > 0 ? 0.2 : (diff < 0 ? -0.1 : 0);
          return parseFloat((prev + step).toFixed(1));
        });
      }

      setBattery(prev => {
        const newBattery = updateBattery(mode, prev);
        localStorage.setItem(`battery-${device.name}`, newBattery.toFixed(1));
        return parseFloat(newBattery.toFixed(1));
      });
    }, 1000);

    return () => {
      clearInterval(interval);
      clearTimeout(timeout);
    };
  }, [mode, device?.targetTemp, temperatureStarted, device?.name]);

  // Fonction pour ajuster la température
  const handleTempChange = (e) => {
    device.targetTemp = parseFloat(e.target.value);
  };

  // Toggle entre "branché" et "chargé"
  const toggleMode = () => {
    const newMode = mode === 'branché' ? 'chargé' : 'branché';
    setMode(newMode);
    device.mode = newMode; // Mettre à jour la variable mode dans le device
  };

  // Fonction pour allumer/éteindre le chauffage
  const toggleHeating = () => {
    setHeatingOn(prevState => !prevState);
  };

  return (
    <div className="thermostat-controls">
      {/* Bouton pour allumer/éteindre le chauffage */}
      <button 
        className="switch-heating"
        onClick={toggleHeating}
        style={{ marginBottom: '10px' }}
      >
        {heatingOn ? 'Éteindre chauffage' : 'Allumer chauffage'}
      </button>

      {/* Affichage de la molette de réglage de température seulement si le chauffage est allumé */}
      {heatingOn && (
        <div className="temperature-control">
          <label htmlFor="tempRange">Réglage de la température</label>
          <input
            id="tempRange"
            type="range"
            min="10"
            max="30"
            step="0.5"
            value={device.targetTemp}
            onChange={handleTempChange}
          />
          <span>{device.targetTemp}°C</span>
        </div>
      )}

      {/* Affichage de l'état du mode d'alimentation (Branché / Chargé) */}
      <div className="power-mode">
        <button
          className="switch-mode"
          onClick={toggleMode}
          style={{ marginBottom: '10px' }}
        >
          {mode === 'branché' ? 'Chargé' : 'Branché'}
        </button>
      </div>
    </div>
  );
}

export default ThermostatControls;
