import React, { useState } from 'react';
import '../styles/SmartDeviceCard.css';
import ThermostatControls from './ThermostatControls';
import LampControls from './LampControls';
import CameraControls from './CameraControls';

function SmartDeviceCard({ device }) {
  const [currentTemp, setCurrentTemp] = useState(device?.currentTemp ?? 20);
  const [heatingOn, setHeatingOn] = useState(false);  // État pour le thermostat
  const [lampOn, setLampOn] = useState(false);        // État pour la lampe
  const [cameraOn, setCameraOn] = useState(false);    // État pour la caméra

  // Fonction pour mettre à jour la température actuelle
  const handleCurrentTempChange = (newTemp) => {
    setCurrentTemp(newTemp);
  };

  // Fonction pour gérer l'état du chauffage
  const toggleHeating = () => {
    setHeatingOn(prevState => !prevState);
  };

  // Fonction pour gérer l'état de la lampe
  const toggleLamp = () => {
    setLampOn(prevState => !prevState);
  };

  // Fonction pour gérer l'état de la caméra
  const toggleCamera = () => {
    setCameraOn(prevState => !prevState);
  };

  // Retour JSX — fallback intégré ici
  if (!device) {
    return <div className="smart-device-card">Aucun appareil sélectionné</div>;
  }

  return (
    <div className="smart-device-card">
      <h3>{device.name || 'Nom inconnu'}</h3>

      {/* Affichage des infos génériques */}
      {device.type === 'thermostat' && (
        <>
          <p><strong>Temp. actuelle :</strong> {currentTemp.toFixed(1)}°C</p>
          <p><strong>Temp. cible :</strong> {typeof device.targetTemp === 'number' ? `${device.targetTemp.toFixed(1)}°C` : 'N/A'}</p>
          <button onClick={toggleHeating}>
            {heatingOn ? 'Éteindre chauffage' : 'Allumer chauffage'}
          </button>
        </>
      )}

      {device.type === 'lamp' && (
        <>
          <p><strong>État lampe :</strong> {lampOn ? 'Allumée' : 'Éteinte'}</p>
          <button onClick={toggleLamp}>
            {lampOn ? 'Éteindre lampe' : 'Allumer lampe'}
          </button>
        </>
      )}

      {device.type === 'camera' && (
        <>
          <p><strong>État caméra :</strong> {cameraOn ? 'Allumée' : 'Éteinte'}</p>
          <button onClick={toggleCamera}>
            {cameraOn ? 'Éteindre caméra' : 'Allumer caméra'}
          </button>
        </>
      )}

      <p><strong>Connectivité :</strong> {device.connectivity || 'Non spécifiée'}</p>
      <p className={`status ${device.status ? device.status.toLowerCase() : ''}`}>
        <strong>État :</strong> {device.status || 'Inconnu'}
      </p>

      <br />
      <hr />

      {/* Contrôles spécifiques au type d’appareil */}
      {device.type === 'thermostat' && (
        <ThermostatControls 
          device={device} 
          onCurrentTempChange={handleCurrentTempChange}  // Passer la fonction pour mettre à jour la température actuelle
        />
      )}
      {device.type === 'lamp' && <LampControls />}
      {device.type === 'camera' && <CameraControls />}
    </div>
  );
}

export default SmartDeviceCard;
