import React, { useState, useEffect } from 'react';
import '../styles/Auth.css';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [role, setRole] = useState('');
    const [users, setUsers] = useState([]);

    // Nouveaux Ã©tats pour afficher l'email aprÃ¨s connexion
    const [isLoggedIn, setIsLoggedIn] = useState(false); // VÃ©rifie si l'utilisateur est connectÃ©
    const [userEmail, setUserEmail] = useState('');
    const [loginError, setLoginError] = useState(''); 

    useEffect(() => {
      if (role === 'admin') {
          fetchUsers();
      }
  }, [role]);

  const fetchUsers = () => {
    if (!userEmail) {
        console.error("âŒ Erreur : L'email de l'admin est manquant !");
        return;
    }

    const data = new URLSearchParams();
    data.append('admin_email', userEmail);  // Envoie l'email de l'admin

    console.log("ðŸ“¤ Envoi de l'email admin :", userEmail);  // VÃ©rifie ce qui est envoyÃ©

    fetch("http://localhost:8000/GetUsers.php", {
        method: "POST",  // Assure-toi que la mÃ©thode est bien POST
        headers: {
            "Content-Type": "application/x-www-form-urlencoded"  // Indique que c'est une requÃªte de type formulaire
        },
        body: data,  // Envoie les donnÃ©es dans le corps de la requÃªte
    })
    .then(response => response.text()) // Traite la rÃ©ponse comme du texte brut pour le dÃ©bogage
    .then(data => {
        console.log("ðŸ“¥ RÃ©ponse brute du serveur:", data);  // Affiche la rÃ©ponse dans la console
        try {
            const jsonData = JSON.parse(data);  // Tente de convertir la rÃ©ponse en JSON
            if (Array.isArray(jsonData)) {
                setUsers(jsonData);  // Si c'est un tableau, mets Ã  jour les utilisateurs
            } else {
                console.error("âŒ Erreur : La rÃ©ponse n'est pas un tableau:", jsonData);
                setUsers([]);  // Sinon, on met Ã  jour avec un tableau vide
            }
        } catch (error) {
            console.error("âŒ Erreur JSON:", error, data);  // Si la rÃ©ponse n'est pas valide, on affiche l'erreur
            setUsers([]);
        }
    })
      .catch(error => console.error("âŒ Erreur rÃ©cupÃ©ration utilisateurs:", error));
    };


    const handleDeleteUser = (targetEmail) => {
      if (!window.confirm(`Voulez-vous vraiment supprimer l'utilisateur ${targetEmail} ?`)) {
          return;
      }

      const data = new URLSearchParams();
      data.append('email', targetEmail);

      fetch("http://localhost:8000/DeleteUser.php", {
          method: "POST",
          headers: {
              "Content-Type": "application/x-www-form-urlencoded",
          },
          body: data,
      })
      .then(response => response.text())
      .then((data) => {
          if (data === "success") {
              alert("Utilisateur supprimÃ© avec succÃ¨s !");
              setUsers(users.filter(user => user.email !== targetEmail)); // Mise Ã  jour dynamique
          } else {
              alert("âŒ Erreur lors de la suppression !");
          }
      })
      .catch((error) => {
          console.error("âŒ Erreur suppression utilisateur:", error);
      });
    };



    const handleLoginSubmit = (e) => {
      e.preventDefault();
    // Affichage des valeurs avant l'envoi
    console.log("DonnÃ©es envoyÃ©es : ", {email, password });

      // CrÃ©e les donnÃ©es Ã  envoyer
      const data = new URLSearchParams();
      data.append('email', email);
      data.append('password', password);


      // recupere des donnÃ©es au serveur PHP
      fetch("http://localhost:8000/LogIn.php", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: data,
      })
      .then((response) => response.text())  // Lecture de la rÃ©ponse en texte brut
      .then((data) => {
        console.log('RÃ©ponse du serveur:', data);  // Affiche la rÃ©ponse du serveur
        if (data.startsWith('success')) {
          const [, userEmail, userRole] = data.split('|');
          console.log("âœ… Email rÃ©cupÃ©rÃ©:", userEmail);
          console.log("âœ… RÃ´le rÃ©cupÃ©rÃ©:", userRole);
          setIsLoggedIn(true);
          setEmail(userEmail);
          setUserEmail(userEmail);
          setRole(userRole);
          setLoginError("");  // RÃ©initialiser l'erreur
        } else {
          setLoginError(data);  // Afficher l'erreur reÃ§ue
        }
      })
      .catch((error) => {
        console.error("Erreur:", error);
      });
    };



    const handleChangePasswordSubmit = (e) => {
      e.preventDefault();
      if (!userEmail || !role) {
        console.error("Erreur : Email ou rÃ´le manquant !");
        return;
    }

      // CrÃ©e les donnÃ©es Ã  envoyer pour changer le mot de passe
      const data = new URLSearchParams();
      data.append('userEmail', userEmail);
      data.append('newPassword', newPassword);
      data.append('role', role);

      fetch("http://localhost:8000/ChangePassword.php", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: data,
      })
        .then((response) => response.text())
        .then((data) => {
        })
        .catch((error) => {
          console.error("Erreur:", error);
        });
    };


    return (
      <div>
          {!isLoggedIn ? (
              <form onSubmit={handleLoginSubmit}>
                  <label>Email :
                      <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
                  </label>
                  <br />
                  <label>Mot de passe :
                      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                  </label>
                  <br />
                  {loginError && <p style={{ color: 'red' }}>{loginError}</p>}
                  <button type="submit">Se connecter</button>
              </form>
          ) : (
              <div>
                  <h2>Bienvenue, {userEmail}!</h2>
                  {role === 'admin' ? (
                      <div>
                          <h3>Liste des utilisateurs</h3>
                          <table border="1">
                              <thead>
                                  <tr>
                                      <th>Email</th>
                                      <th>RÃ´le</th>
                                      <th>Action</th>
                                  </tr>
                              </thead>
                              <tbody>
                                  {users.map((user, index) => (
                                      <tr key={index}>
                                          <td>{user.email}</td>
                                          <td>{user.role}</td>
                                          <td>
                                              <button onClick={() => handleDeleteUser(user.email)}>ðŸ—‘ Supprimer</button>
                                          </td>
                                      </tr>
                                  ))}
                              </tbody>
                          </table>
                      </div>
                  ) : (
                      <div>
                          <h3>Modifier votre mot de passe</h3>
                          <form>
                              <label>Nouveau mot de passe :
                                  <input type="password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} />
                              </label>
                              <br />
                              <button type="submit">Modifier le mot de passe</button>
                          </form>
                      </div>
                  )}
              </div>
          )}
      </div>
  );
}




export default Login;
