import React, { useState, useEffect } from 'react';
import '../styles/Auth.css';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [role, setRole] = useState('');
    const [users, setUsers] = useState([]);

    // Nouveaux Ã©tats pour afficher l'email aprÃ¨s connexion
    const [isLoggedIn, setIsLoggedIn] = useState(false); // VÃ©rifie si l'utilisateur est connectÃ©
    const [userEmail, setUserEmail] = useState('');
    const [loginError, setLoginError] = useState(''); 

    useEffect(() => {
      console.log("Le rÃ´le mis Ã  jour:", role);

    }, [role]); // VÃ©rifie que le rÃ´le change bien

    const fetchUsers = () => {
      fetch("http://localhost:8000/GetUsers.php")
          .then(response => response.json())
          .then(data => {
              console.log("ðŸ“¥ Utilisateurs rÃ©cupÃ©rÃ©s:", data);
              setUsers(data);
          })
          .catch(error => console.error("âŒ Erreur rÃ©cupÃ©ration utilisateurs:", error));
    };

    const handleLoginSubmit = (e) => {
      e.preventDefault();
    // Affichage des valeurs avant l'envoi
    console.log("DonnÃ©es envoyÃ©es : ", {email, password });

      // CrÃ©e les donnÃ©es Ã  envoyer
      const data = new URLSearchParams();
      data.append('email', email);
      data.append('password', password);


      // recupere des donnÃ©es au serveur PHP
      fetch("http://localhost:8000/LogIn.php", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: data,
      })
      .then((response) => response.text())  // Lecture de la rÃ©ponse en texte brut
      .then((data) => {
        console.log('RÃ©ponse du serveur:', data);  // Affiche la rÃ©ponse du serveur
        if (data.startsWith('success')) {
          const [, userEmail, userRole] = data.split('|');
          console.log("âœ… Email rÃ©cupÃ©rÃ©:", userEmail);
          console.log("âœ… RÃ´le rÃ©cupÃ©rÃ©:", userRole);
          setIsLoggedIn(true);
          setEmail(userEmail);
          setRole(userRole);
          setLoginError("");  // RÃ©initialiser l'erreur
        } else {
          setLoginError(data);  // Afficher l'erreur reÃ§ue
        }
      })
      .catch((error) => {
        console.error("Erreur:", error);
      });
    };
    const handleChangePasswordSubmit = (e) => {
      e.preventDefault();
      if (!userEmail || !role) {
        console.error("Erreur : Email ou rÃ´le manquant !");
        return;
    }

      // CrÃ©e les donnÃ©es Ã  envoyer pour changer le mot de passe
      const data = new URLSearchParams();
      data.append('userEmail', userEmail);
      data.append('newPassword', newPassword);
      data.append('role', role);

      fetch("http://localhost:8000/ChangePassword.php", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: data,
      })
        .then((response) => response.text())
        .then((data) => {
        })
        .catch((error) => {
          console.error("Erreur:", error);
        });
    };

    return (
      <div>
          {!isLoggedIn ? (
              // Formulaire de connexion
              <form onSubmit={handleLoginSubmit}>
                  <label>
                      Email :
                      <input 
                          type="email" 
                          value={email} 
                          onChange={(e) => setEmail(e.target.value)} 
                      />
                  </label>
                  <br />
                  <label>
                      Mot de passe :
                      <input 
                          type="password" 
                          value={password} 
                          onChange={(e) => setPassword(e.target.value)} 
                      />
                  </label>
                  <br />
                  {loginError && <p style={{ color: 'red' }}>{loginError}</p>}
                  <button type="submit">Se connecter</button>
              </form>
          ) : (
            <div>
                <h2>Bienvenue, {userEmail}!</h2>
                {role === 'admin' ? (
                    // ðŸŒŸ Affichage du tableau des utilisateurs pour l'admin
                    <div>
                        <h3>Liste des utilisateurs</h3>
                        <table border="1">
                            <thead>
                                <tr>
                                    <th>Email</th>
                                    <th>RÃ´le</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users.map((user, index) => (
                                    <tr key={index}>
                                        <td>{user.email}</td>
                                        <td>{user.role}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                ) : (
                    // ðŸŒŸ Formulaire de changement de mot de passe pour un user normal
                    <div>
                        <h3>Modifier votre mot de passe</h3>
                        <form>
                            <label>Nouveau mot de passe :
                                <input type="password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} />
                            </label>
                            <br />
                            <button type="submit">Modifier le mot de passe</button>
                        </form>
                    </div>
                )}
            </div>
        )}
    </div>
  );
}




export default Login;
