import React, { useState, useEffect } from 'react';

function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const [adminEmail, setAdminEmail] = useState(''); // Email de l'admin (récupéré après la connexion)

  // Récupérer la liste des utilisateurs
  const fetchUsers = () => {
    fetch(`http://localhost:8000/GetUsers.php?email=${adminEmail}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.status === 'success') {
          setUsers(data.users);
        } else {
          alert(data.message);
        }
      })
      .catch((error) => console.error("Erreur:", error));
  };

  const handleDeleteUser = (userId) => {
    fetch('http://localhost:8000/DeleteUser.php', {
      method: 'POST',
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: new URLSearchParams({
        adminEmail,
        userId,
      }),
    })
      .then((response) => response.text())
      .then((message) => {
        alert(message);
        fetchUsers();  // Rafraîchir la liste des utilisateurs après suppression
      })
      .catch((error) => console.error("Erreur:", error));
  };

  useEffect(() => {
    fetchUsers(); // Récupérer la liste des utilisateurs à l'initialisation
  }, [adminEmail]);

  return (
    <div>
      <h2>Tableau de bord Admin</h2>
      <ul>
        {users.map((user) => (
          <li key={user.id}>
            {user.email}
            <button onClick={() => handleDeleteUser(user.id)}>Supprimer</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminDashboard;
