import React, { useState } from 'react';
import '../styles/Auth.css';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const [message, setMessage] = useState('');
    // Nouveaux états pour afficher l'email après connexion
    const [isLoggedIn, setIsLoggedIn] = useState(false); // Vérifie si l'utilisateur est connecté
    const [userEmail, setUserEmail] = useState('');
    const [loginError, setLoginError] = useState(''); 

    const handleSubmit = (e) => {
      e.preventDefault();
      // Affichage des valeurs avant l'envoi
      console.log("Données envoyées : ", { email, password });

      // Crée les données à envoyer
      const data = new URLSearchParams();
      data.append('email', email);
      data.append('password', password);


      // recupere des données au serveur PHP
      fetch("http://localhost:8000/LogIn.php", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: data,
      })
      .then((response) => response.json()) // Lire la réponse sous forme de texte
      .then((data) => {
        console.log('Réponse du serveur:', data);
        if (data.status === 'success') {
          setIsLoggedIn(true);
          setUserEmail(data.email); // Mettre l'email de l'utilisateur dans l'état
          setLoginError('');  // Réinitialise l'erreur en cas de succès
        } else {
          setIsLoggedIn(false);  // Si échec, on s'assure que l'état est à false
          setLoginError(data.message);  // Affiche l'erreur retournée par le serveur
        }
      })
      .catch((error) => {
        console.error("Erreur:", error);
        setLoginError('Erreur de connexion, veuillez réessayer.');
      });
    };
  return (
    <div>
       {/* Affichage du message d'erreur s'il y en a un */}
       {loginError && <p style={{ color: 'red' }}>{loginError}</p>}
      {!isLoggedIn ? (
        <form onSubmit={handleSubmit}>
          <label>
            Email :
            <input 
              type="email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
            />
          </label>
          <br />
          <label>
            Mot de passe :
            <input 
              type="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
            />
          </label>
          <br />
          <br />
          <button type="submit">LogIn</button>
        </form>
      ) : (
        <div>
          <h2>Connexion réussie !</h2>
          <p>Email : {userEmail}</p>
          <p>Mot de passe : {password}</p>
        </div>
      )}
    </div>
  );
}
export default Login;
