import React, { useState, useEffect } from 'react';
//import '../styles/Auth.css';
import '../styles/LogIn.css';  
import { useNavigate } from "react-router-dom";
import { mockData } from '../data/mockData';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [role, setRole] = useState('');
    const [users, setUsers] = useState([]);
    const [attente, setAttente] = useState([]);
    const [messages, setMessages] = useState([]);

    const [isLoggedIn, setIsLoggedIn] = useState(false); 
    const [userEmail, setUserEmail] = useState('');
    const [loginError, setLoginError] = useState('');
    const [selectedDevices, setSelectedDevices] = useState(
      JSON.parse(localStorage.getItem('selectedDevices')) || []  // R√©cup√©rer les appareils s√©lectionn√©s depuis localStorage
    );  


  // V√©rifier l'√©tat de la session au d√©marrage
    useEffect(() => {
        fetch('http://localhost:8000/checkSession.php', {
          method: 'GET',
          credentials: 'include', // inclure les cookies pour v√©rifier la session
        })
          .then(response => response.json())
          .then(data => {
            if (data.success) {
              setIsLoggedIn(true);
              setUserEmail(data.email); 
              setRole(data.role); 
            }
          })
          .catch(error => {
            console.error('Erreur lors de la v√©rification de la session:', error);
          });
      }, []);

    const handleLoginSubmit = (e) => {
      e.preventDefault();
    // Affichage des valeurs avant l'envoi
    console.log("Donn√©es envoy√©es : ", {email, password });

      // Cr√©e les donn√©es √† envoyer
      const data = new URLSearchParams();
      data.append('email', email);
      data.append('password', password);


      // recupere des donn√©es au serveur PHP
      fetch("http://localhost:8000/LogIn.php", {
        method: "POST",
        credentials: "include", 
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: data,
      })
      .then((response) => response.text())  // Lecture de la r√©ponse en texte brut
      .then((data) => {
        console.log('R√©ponse du serveur:', data);  // Affiche la r√©ponse du serveur
        if (data.startsWith('success')) {
          const [, userEmail, userRole] = data.split('|');
          console.log(" Email r√©cup√©r√©:", userEmail);
          console.log("R√¥le r√©cup√©r√©:", userRole);
          setIsLoggedIn(true);
          setEmail(userEmail);
          setUserEmail(userEmail);
          setRole(userRole);
          setLoginError("");  // R√©initialiser l'erreur
        } else {
          setLoginError(data);  // Afficher l'erreur re√ßue
        }
      })
      .catch((error) => {
        console.error("Erreur:", error);
      });
    };



    const handleLogout = () => {
      fetch('http://localhost:8000/logout.php', {
        method: 'GET',
        credentials: 'include', 
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        }, 
      })
      .then((response) => response.json()) 
      .then((data) => {
        if (data.success) {
          console.log('D√©connexion r√©ussie:', data.message); // Affiche dans la console
          alert(data.message); // Affiche un message √† l'utilisateur
          setIsLoggedIn(false);
          setUserEmail('');
          navigate('/'); 
        } else {
          console.error('Erreur de d√©connexion:', data.message);
        }
      })
      .catch((error) => {
        console.error('Erreur r√©seau:', error);
      });
    };





    const [passwordMessage, setPasswordMessage] = useState('');

    const handleChangePasswordSubmit = (e) => {
        e.preventDefault();

        if (!userEmail || !role) {
            console.error("Erreur : Email ou r√¥le manquant !");
            setPasswordMessage(" Une erreur est survenue.");
            return;
        }

        if (!newPassword.trim()) {
            setPasswordMessage(" Le mot de passe ne peut pas √™tre vide.");
            return;
        }
        console.log("üîç Donn√©es envoy√©es :", { userEmail, newPassword });
        // Cr√©ation des donn√©es √† envoyer
        const data = new URLSearchParams();
        data.append('userEmail', userEmail);
        data.append('newPassword', newPassword);
        data.append('role', role);

        fetch("http://localhost:8000/ChangePassword.php", {
            method: "POST",
            headers: {
                "Content-Type": "application/x-www-form-urlencoded",
            },
            body: data,
        })
        .then(response => response.text()) 
        .then((serverMessage) => {
            console.log("R√©ponse du serveur :", serverMessage);

            if (serverMessage === "success") {
                setPasswordMessage(" Mot de passe chang√© avec succ√®s !");
            } else {
                setPasswordMessage("" + serverMessage);
            }
        })
        .catch((error) => {
            console.error("Erreur:", error);
            setPasswordMessage(" Une erreur r√©seau est survenue.");
        });
    };

    const navigate = useNavigate();



    const handleDeleteUser = (targetEmail) => {
      if (!window.confirm(`Voulez-vous vraiment supprimer l'utilisateur ${targetEmail} ?`)) {
          return;
      }

      const data = new URLSearchParams();
      data.append('email', targetEmail);

      fetch("http://localhost:8000/DeleteUser.php", {
          method: "POST",
          headers: {
              "Content-Type": "application/x-www-form-urlencoded",
          },
          body: data,
      })
      .then(response => response.text())
      .then((data) => {
          if (data === "success") {
              alert("Utilisateur supprim√© avec succ√®s !");
              setUsers(users.filter(user => user.email !== targetEmail)); // Mise √† jour dynamique
          } else {
              alert(" Erreur lors de la suppression !");
          }
      })
      .catch((error) => {
          console.error(" Erreur suppression utilisateur:", error);
      });
    };




    useEffect(() => {
      if (isLoggedIn && role === 'admin') {
        fetchUsers();  // Appel uniquement si l'utilisateur est connect√© et admin
      }
    }, [isLoggedIn, role]);
    const fetchUsers = () => {
      const adminEmail = 'admin@example.com';  // L'email de l'admin

      console.log(" Envoi de l'email admin :", adminEmail);  // Affiche l'email envoy√©

      const data = new URLSearchParams();
      data.append('admin_email', adminEmail);  // Ajoute l'email dans les donn√©es envoy√©es

      fetch("http://localhost:8000/GetUsers.php", {
        method: "POST",  // M√©thode POST pour envoyer les donn√©es
        headers: {
          "Content-Type": "application/x-www-form-urlencoded"  // Formulaire x-www-form-urlencoded
        },
        body: data  // Envoi des donn√©es avec l'email de l'admin
      })
      .then(response => response.json())  // Traite la r√©ponse comme un JSON
      .then(data => {
        console.log(" R√©ponse brute du serveur:", data);

        if (data.users && data.attente) {
          setUsers(data.users);  // Mets √† jour la liste des utilisateurs
          setAttente(data.attente);  // Mets √† jour la liste des utilisateurs en attente
        } else {
          console.error(" Erreur : Pas de donn√©es valides dans la r√©ponse");
        }
      })
      .catch(error => {
        console.error(" Erreur r√©cup√©ration utilisateurs et liste d'attente:", error);
      });
    };



    const handleMoveToUser = (email) => {
      // Demander √† l'utilisateur de confirmer l'action
      if (window.confirm(`Voulez-vous vraiment d√©placer ${email} de la liste d'attente vers la liste des utilisateurs ?`)) {

        // Pr√©parer les donn√©es √† envoyer
        const data = new URLSearchParams();
        data.append('email', email); // L'email de l'utilisateur √† d√©placer
        console.log("email a transferer:",email); 

        // Effectuer la requ√™te pour d√©placer l'utilisateur
        fetch("http://localhost:8000/MoveToUser.php", {
          method: "POST",
          headers: {
            "Content-Type": "application/x-www-form-urlencoded"
          },
          body: data
        })
        .then(response => response.text())
        .then(response => {
          if (response === 'success') {
            alert(`${email} a √©t√© d√©plac√© avec succ√®s vers la liste des utilisateurs.`);
            // Mettre √† jour les listes localement apr√®s le d√©placement
            setAttente(attente.filter(user => user.email !== email)); // Enlever de la liste d'attente
            setUsers([...users, { email, role: 'user' }]); // Ajouter √† la liste des utilisateurs
          } else {
            alert(" Une erreur est survenue lors du d√©placement.");
          }
        })
        .catch(error => {
          console.error(" Erreur lors du d√©placement :", error);
        });
      }
    };
    const handleDeviceSelection = (event) => {
      const { value, checked } = event.target;
      setSelectedDevices((prevSelectedDevices) => {
          const updatedDevices = checked
              ? [...prevSelectedDevices, value]  // Ajoute l'appareil si coch√©
              : prevSelectedDevices.filter(device => device !== value);  // Enl√®ve l'appareil si d√©coch√©
          localStorage.setItem('selectedDevices', JSON.stringify(updatedDevices));  // Sauvegarde dans localStorage
          return updatedDevices;
      });
  };

  const handleSubmitDevices = () => {
      navigate('/', { state: { selectedDevices } });  // Passe les appareils s√©lectionn√©s √† Home
  };

  useEffect(() => {
    if (isLoggedIn && role === 'admin') {
        fetchMessages();  // Appel pour r√©cup√©rer les messages
    }
}, [isLoggedIn, role]);

const fetchMessages = () => {
    fetch('http://localhost:8000/GetMessages.php', {
        method: 'GET',
        credentials: 'include',
    })
    .then(response => response.json())
    .then(data => {
        console.log('Messages r√©cup√©r√©s:', data);
        if (data.messages) {
            setMessages(data.messages);  // Met √† jour l'√©tat des messages
        } else {
            console.error('Erreur lors de la r√©cup√©ration des messages.');
        }
    })
    .catch(error => {
        console.error('Erreur de r√©cup√©ration des messages:', error);
    });
};





    return (
      <div className="login-container">
        {!isLoggedIn ? (
          <form onSubmit={handleLoginSubmit}>
            <label>Email :
              <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
            </label>
            <br />
            <label>Mot de passe :
              <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </label>
            <br />
            {loginError && <p style={{ color: 'red' }}>{loginError}</p>}
            <button type="submit">Se connecter</button>
          </form>
        ) : (
          <div>
            <h2>Bienvenue, {userEmail}!</h2>
            {role === 'admin' ? (
              <div>
                <h3>Liste des utilisateurs</h3>
                <table border="1">
                  <thead>
                    <tr>
                      <th>Email</th>
                      <th>R√¥le</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {users.map((user, index) => (
                      <tr key={index}>
                        <td>{user.email}</td>
                        <td>{user.role}</td>
                        <td>
                          <button onClick={() => handleDeleteUser(user.email)}>Supprimer</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>

                <h3>Liste d'attente</h3>
                <table border="1">
                  <thead>
                    <tr>
                      <th>Email</th>
                      <th>R√¥le</th>
                      <th>Type</th>
                    </tr>
                  </thead>
                  <tbody>
                    {attente.map((user, index) => (
                      <tr key={index}>
                        <td>{user.email}</td>
                        <td>{user.role}</td>
                        <td>{user.type}</td>
                        <td>
                          <button onClick={() => handleMoveToUser(user.email)}>Passer √† utilisateur</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <div>
                    <h3>Messages des utilisateurs</h3>
                    {messages.length > 0 ? (
                    <table border="1">
                      <thead>
                        <tr>
                          <th>Email</th>
                          <th>Message</th>
                          <th>Date</th>
                        </tr>
                      </thead>
                      <tbody>
                        {messages.map((message, index) => (
                          <tr key={index}>
                            <td>{message.user_email}</td>
                            <td>{message.message}</td>
                            <td>{message.sent_at}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  ) : (
                    <p>Aucun message √† afficher.</p>
                  )}
              </div>

                <h3>S√©lectionnez des appareils √† afficher</h3>
                    <form>
                        {mockData.connectedDevices.map((device) => (
                            <div key={device.id}>
                                <input
                                    type="checkbox"
                                    id={`device-${device.id}`}
                                    value={device.id}
                                    checked={selectedDevices.includes(device.id)}
                                    onChange={handleDeviceSelection}
                                />
                                <label htmlFor={`device-${device.id}`}>{device.name}</label>
                            </div>
                        ))}
                    </form>
                    <button onClick={handleSubmitDevices} style={{ marginTop: '20px' }}>
                      ‚¨Ö Appliquer les modifications
                    </button>
                 {/* Nouveau bouton pour retourner √† l'accueil */}  
                  <button onClick={() => navigate('/')} style={{ marginTop: '10px' }}>
                      ‚¨Ö Retour √† l'accueil
                  </button>
              </div>
            ) : (
              <div>
                <h3>Modifier votre mot de passe</h3>
                <form onSubmit={handleChangePasswordSubmit}>
                  <label>Nouveau mot de passe :
                    <input type="password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} />
                  </label>
                  <br />
                  <button type="submit">Modifier le mot de passe</button>
                </form>
                {/*  Affichage du message de succ√®s ou d'erreur */}
                  {passwordMessage && (
                      <p style={{ color: passwordMessage.startsWith('‚úÖ') ? 'green' : 'red' }}>
                          {passwordMessage}
                      </p>
                )}
                {/* Nouveau bouton pour retourner √† l'accueil */}
                <button onClick={() => navigate('/')} style={{ marginTop: '10px' }}>
                    ‚¨Ö Retour √† l'accueil
                </button>
                <button onClick={handleLogout}>Se d√©connecter</button>
              </div>
            )}
          </div>
        )}
      </div>
    );

}

export default Login;
