import React, { useState } from 'react';
import '../styles/Auth.css';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');

    // Nouveaux états pour afficher l'email après connexion
    const [isLoggedIn, setIsLoggedIn] = useState(false); // Vérifie si l'utilisateur est connecté
    const [userEmail, setUserEmail] = useState('');
    const [loginError, setLoginError] = useState(''); 

    const handleLoginSubmit = (e) => {
      e.preventDefault();
    // Affichage des valeurs avant l'envoi
    console.log("Données envoyées : ", {email, password });

      // Crée les données à envoyer
      const data = new URLSearchParams();
      data.append('email', email);
      data.append('password', password);


      // recupere des données au serveur PHP
      fetch("http://localhost:8000/LogIn.php", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: data,
      })
      .then((response) => response.text())  // Lecture de la réponse en texte brut
      .then((data) => {
        console.log('Réponse du serveur:', data);  // Affiche la réponse du serveur
        if (data === "Connexion réussie") {
          setIsLoggedIn(true);
          setUserEmail(email);
          setLoginError("");  // Réinitialiser l'erreur
        } else {
          setLoginError(data);  // Afficher l'erreur reçue
        }
      })
      .catch((error) => {
        console.error("Erreur:", error);
      });
    };
    const handleChangePasswordSubmit = (e) => {
      e.preventDefault();

      // Crée les données à envoyer pour changer le mot de passe
      const data = new URLSearchParams();
      data.append('email', userEmail);
      data.append('newPassword', newPassword);

      fetch("http://localhost:8000/ChangePassword.php", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: data,
      })
        .then((response) => response.text())
        .then((data) => {
          setMessage(data);  // Affiche le message du serveur
        })
        .catch((error) => {
          console.error("Erreur:", error);
        });
    };

  return (
    <div>
      {!isLoggedIn ? (
        // Formulaire de connexion
        <form onSubmit={handleLoginSubmit}>
          <label>
            Email :
            <input 
              type="email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
            />
          </label>
          <br />
          <label>
            Mot de passe :
            <input 
              type="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
            />
          </label>
          <br />
          <button type="submit">Se connecter</button>
        </form>
      ) : (
        // Une fois connecté, affichage de l'email et formulaire de changement de mot de passe
        <div>
          <h2>Connexion réussie !</h2>
          <p>Email : {userEmail}</p>

          <h3>Modifier votre mot de passe</h3>
          <form onSubmit={handleChangePasswordSubmit}>
            <label>
              Nouveau mot de passe :
              <input 
                type="password" 
                value={newPassword} 
                onChange={(e) => setNewPassword(e.target.value)} 
              />
            </label>
            <br />
            <button type="submit">Modifier le mot de passe</button>
          </form>
          {message && <p>{message}</p>}  {/* Affiche le message du serveur */}
        </div>
      )}
    </div>
  );
}

export default Login;
