import React, { useState, useEffect } from 'react';

function LockControls({ updateDeviceStatus }) {
  const [isLocked, setIsLocked] = useState(true); // L'état initial est "verrouillé"

  // Fonction pour basculer l'état du cadenas
  const handleToggleLock = () => {
    setIsLocked(prevState => !prevState);
  };

  // Mise à jour du statut dans le composant parent
  useEffect(() => {
    updateDeviceStatus('lock', isLocked ? 'Verrouillé' : 'Déverrouillé');
  }, [isLocked, updateDeviceStatus]);

  return (
    <div className="lock-controls">
      <div className="lock-status">
        <span>{isLocked ? 'Cadenas Verrouillé' : 'Cadenas Déverrouillé'}</span>
      </div>

      <div className="lock-controls-buttons">
        <button className="switch" onClick={handleToggleLock}>
          {isLocked ? 'Déverrouiller' : 'Verrouiller'}
        </button>
      </div>
    </div>
  );
}

export default LockControls;
