import React, { useState } from 'react';
import '../styles/Auth.css';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const [message, setMessage] = useState('');
    // Nouveaux états pour afficher l'email après connexion
    const [isLoggedIn, setIsLoggedIn] = useState(false); // Vérifie si l'utilisateur est connecté
    const [userEmail, setUserEmail] = useState('');
    const [loginError, setLoginError] = useState(''); 

    const handleSubmit = (e) => {
      e.preventDefault();
    // Affichage des valeurs avant l'envoi
    console.log("Données envoyées : ", {email, password });

      // Crée les données à envoyer
      const data = new URLSearchParams();
      data.append('email', email);
      data.append('password', password);


      // recupere des données au serveur PHP
      fetch("http://localhost:8000/LogIn.php", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: data,
      })
      .then((response) => response.text())  // Lecture de la réponse en texte brut
      .then((data) => {
        console.log('Réponse du serveur:', data);  // Affiche la réponse du serveur
        if (data === "Connexion réussie") {
          setIsLoggedIn(true);
          setUserEmail(email);
          setLoginError("");  // Réinitialiser l'erreur
        } else {
          setLoginError(data);  // Afficher l'erreur reçue
        }
      })
      .catch((error) => {
        console.error("Erreur:", error);
      });
    };
  return (
    <div>
       {/* Affichage du message d'erreur s'il y en a un */}
       {loginError && <p style={{ color: 'red' }}>{loginError}</p>}
      {!isLoggedIn ? (
        <form onSubmit={handleSubmit}>
          <label>
            Email :
            <input 
              type="email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
            />
          </label>
          <br />
          <label>
            Mot de passe :
            <input 
              type="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
            />
          </label>
          <br />
          <br />
          <button type="submit">LogIn</button>
        </form>
      ) : (
        <div>
          <h2>Connexion réussie !</h2>
          <p>Email : {userEmail}</p>
          <p>Mot de passe : {password}</p>
        </div>
      )}
    </div>
  );
}
export default Login;
