import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Home.css';
import '../styles/SearchBar.css';
import '../styles/SearchResults.css';
import SearchBar from '../components/SearchBar';
import { mockData } from '../data/mockData';

function Home() {
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = (searchParams) => {
    const { term, type, filter } = searchParams;
    let results = [];

    if (type && mockData[type]) {
      // Si un type est sélectionné, filtrer selon le type et le filtre
      results = mockData[type].filter(item => {
        const matchesTerm = !term || (
          item.name.toLowerCase().includes(term.toLowerCase()) ||
          item.description.toLowerCase().includes(term.toLowerCase())
        );
        const matchesFilter = filter === 'all' || item.type === filter;
        return matchesTerm && matchesFilter;
      });
    } else if (term) {
      // Si aucun type n'est sélectionné mais il y a un terme de recherche
      Object.values(mockData).forEach(typeData => {
        const typeResults = typeData.filter(item => 
          item.name.toLowerCase().includes(term.toLowerCase()) ||
          item.description.toLowerCase().includes(term.toLowerCase())
        );
        results = [...results, ...typeResults];
      });
    } else {
      // Si aucun type n'est sélectionné et pas de terme de recherche, afficher tous les résultats
      Object.values(mockData).forEach(typeData => {
        results = [...results, ...typeData];
      });
    }

    setSearchResults(results);
  };

  return (
    <div className="home-container">
      <header className="home-header">
        <h1>Bienvenue à "nom ville"</h1>
        <SearchBar onSearch={handleSearch} />
      </header>

      <main className="home-main">
        {searchResults.length > 0 && (
          <div className="search-results">
            <h3>Résultats de la recherche</h3>
            <div className="results-grid">
              {searchResults.map(item => (
                <div key={item.id} className="result-card">
                  <h4>{item.name}</h4>
                  <p>{item.description}</p>
                  {item.date && <p>Date: {item.date}</p>}
                  <span className="result-type">{item.type}</span>
                </div>
              ))}
            </div>
          </div>
        )}
        <div className="home-content">
          <h2>Découvrez notre ville ! </h2>
          <p>Fondée en 1880, "nom ville" est une commune de la région Auvergne-Rhône-Alpes</p>
        </div>

        <div className="home-buttons">
          <Link to="/signup" className="btn btn-signup">S'inscrire</Link>
          <Link to="/login" className="btn btn-login">Se connecter</Link>
          <Link to="/events" className="btn btn-events">Événements à venir</Link>
          <Link to="/places" className="btn btn-places">Lieux incontournables</Link>
          <Link to="/transport" className="btn btn-transport">Me déplacer à "nom ville"</Link>
        </div>
      </main>

      <footer className="home-footer">
        <p></p>
      </footer>
    </div>
  );
}

export default Home;
