import React, { useState, useEffect } from 'react';

function AdminDashboard({ adminEmail }) {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:8000/GetUsers.php?email=${adminEmail}`)
      .then((response) => response.text())
      .then((data) => {
        if (!data.startsWith('Erreur')) {
          setUsers(data.split('|'));
        } else {
          alert(data);
        }
      })
      .catch((error) => console.error('Erreur:', error));
  }, [adminEmail]);

  const handleDeleteUser = (userEmail) => {
    fetch('http://localhost:8000/DeleteUser.php', {
      method: 'POST',
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      body: new URLSearchParams({ adminEmail, userEmail }),
    })
      .then((response) => response.text())
      .then((message) => {
        alert(message);
        setUsers(users.filter((email) => email !== userEmail));
      })
      .catch((error) => console.error('Erreur:', error));
  };

  return (
    <div>
      <h2>Utilisateurs :</h2>
      <ul>
        {users.map((userEmail) => (
          <li key={userEmail}>
            {userEmail} <button onClick={() => handleDeleteUser(userEmail)}>Supprimer</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminDashboard;
