    const toggleLock = () => {
        setIsLocked(prevState => !prevState);
    };

    // Mise à jour de l'état dans le composant parent lorsque l'état du cadenas change
    useEffect(() => {
        updateDeviceStatus('lock', isLocked ? 'Verrouillé' : 'Déverrouillé');
